package zserio.runtime;

import java.sql.SQLException;
import java.util.Set;

/**
 * The interface for write-able SQL database classes generated by Zserio.
 */
public interface SqlDatabase extends SqlDatabaseReader
{
    /**
     * Creates a SQL schema.
     *
     * @throws SQLException If the schema creation fails.
     */
    void createSchema() throws SQLException;

    /**
     * Creates a SQL schema accepting without_rowid tables black list.
     *
     * @param withoutRowIdTableNamesBlackList Names of without_rowid tables which should be created as ordinary
     *                                        rowid tables (without without_rowid).
     *
     * @throws SQLException If the schema creation fails.
     */
    void createSchema(Set<String> withoutRowIdTableNamesBlackList) throws SQLException;

    /**
     * Deletes a SQL schema.
     *
     * @throws SQLException If the schema deletion fails.
     */
    void deleteSchema() throws SQLException;
}
