package zserio.runtime;

import java.io.IOException;

import zserio.runtime.ZserioError;
import zserio.runtime.io.BitStreamReader;

/**
 * Interface for a size calculation for classes generated by Zserio. !@#
 */
public interface ZserioReader
{
    /**
     * Gets the bit length of object stored in bit stream.
     *
     * @param bitPosition Current bit stream position.
     *
     * @return Length of object in number of bits.
     */
    public int bitSizeOf(long bitPosition);

    public void read(BitStreamReader __in) throws IOException, ZserioError;
}
