package zserio.runtime.io;

import java.io.IOException;

import zserio.runtime.array.PackingContext;

/**
 * Interface for writing to bit stream for packable classes generated by Zserio.
 *
 * This interface is used only internally for generated code.
 */
public interface PackableWriter extends Writer
{
    /**
     * Initializes indexed offsets for the object which is an element of a packed array.
     *
     * @param context Packing context node.
     * @param bitPosition Current bit stream position.
     *
     * @return Updated bit stream position which points to the first bit after the array.
     */
    public long initializeOffsets(PackingContext context, long bitPosition);

    /**
     * Writes this object which is an element of a packed array to the given bit stream.
     *
     * @param context Packing context node.
     * @param writer Bit stream writer to use.
     *
     * @throws IOException Throws if the writing failed.
     */
    public void write(PackingContext context, BitStreamWriter writer) throws IOException;
}
