package zserio.runtime.io;

import java.io.IOException;

import zserio.runtime.array.PackingContextNode;

/**
 * Interface for writing to bit stream for classes generated by Zserio.
 */
public interface Writer
{
    /**
     * Initializes indexed offsets.
     *
     * It is supposed that the current bit stream position is zero.
     *
     * @return Updated bit stream position which points to the first bit after the array.
     */
    public long initializeOffsets();

    /**
     * Initializes indexed offsets.
     *
     * @param bitPosition Current bit stream position.
     *
     * @return Updated bit stream position which points to the first bit after the array.
     */
    public long initializeOffsets(long bitPosition);

    /**
     * Initializes indexed offsets for the object which is an element of a packed array.
     *
     * @param contextNode Packing context node.
     * @param bitPosition Current bit stream position.
     *
     * @return Updated bit stream position which points to the first bit after the array.
     */
    public long initializeOffsets(PackingContextNode contextNode, long bitPosition);

    /**
     * Writes this objects to the given bit stream.
     *
     * @param out Bit stream writer to use.
     *
     * @throws IOException Throws if the writing failed.
     */
    public void write(zserio.runtime.io.BitStreamWriter out) throws IOException;

    /**
     * Writes this objects to the given bit stream and optionally calls initializeOffsets.
     *
     * @param out The bit stream writer to use.
     * @param callInitializeOffsets True to call initializeOffsets on the object being written.
     *
     * @throws IOException Throws if the writing failed.
     */
    public void write(BitStreamWriter out, boolean callInitializeOffsets) throws IOException;

    /**
     * Writes this object which is an element of a packed array to the given bit stream.
     *
     * @param contextNode Packing context node.
     * @param writer Bit stream writer to use.
     *
     * @throws IOException Throws if the writing failed.
     */
    public void write(PackingContextNode contextNode, BitStreamWriter writer) throws IOException;
}
